def values(data : dict):
    comm = 0
    coll = 0
    d = 0
    a = 0
    for var in communicative_list:
        val = float(data.get(var))
        comm = comm + (val *2)

    for var in communicative_neg_list:
        val = float(data.get(var))
        comm = comm - val        

    for var in collaborative_list:
        val = float(data.get(var))
        coll = coll + (val *2)

    for var in collaborative_neg_list:
        val = float(data.get(var))
        coll = coll - val
        
    for var in collaborative_pos_list:
        val = float(data.get(var))
        coll = coll + val*4

    for var in accountability:
        val = float(data.get(var))
        a = a + (val *2)

    for var in accountability_neg:
        val = float(data.get(var))
        a = a - val

    for var in drive:
        val = float(data.get(var))
        d = a + (val *2)

    for var in drive_neg:
        val = float(data.get(var))
        d = a - val

    #print(comm)
    #print(coll)
    #print(a)
    #print(d)

    comm = comm/(len(communicative_list)+len(communicative_neg_list))
    coll = coll/(len(collaborative_list)+len(collaborative_neg_list)+len(collaborative_pos_list))
    a = a/(len(accountability)+len(accountability_neg))
    d = d/(len(drive)+len(drive_neg))

    #print(">>>>>>")

    #print(comm)
    #print(coll)
    #print(a)
    #print(d)

    #print(">>>>>>")


    list_pre = {'Accountability':'0', 'Drive': '0', 'Collaborative Spirit': '0', 'Communicative for Impact': '0'}
    
    if comm > 0.0 and comm < .16666:
        list_pre.update({'Communicative for Impact': "Not Assessed"})
    elif comm > .16666 and comm < .333333:
        list_pre.update({'Communicative for Impact': "Unacceptable"})
    elif comm > .333333 and comm < .50000:
        list_pre.update({'Communicative for Impact': "Margina"})
    elif comm > .50000 and comm < .66666666:
        list_pre.update({'Communicative for Impact': "Acceptable"})
    elif comm > .66666666 and comm < .83333333:
        list_pre.update({'Communicative for Impact': "Above Average"})
    else:
        list_pre.update({'Communicative for Impact': "Outstanding"})

    if coll > 0.0 and coll < .16666:
        list_pre.update({'Collaborative Spirit': "Not Assessed"})
    elif coll > .16666 and coll < .333333:
        list_pre.update({'Collaborative Spirit': "Unacceptable"})
    elif coll > .333333 and coll < .50000:
        list_pre.update({'Collaborative Spirit': "Margina"})
    elif coll > .50000 and coll < .66666666:
        list_pre.update({'Collaborative Spirit': "Acceptable"})
    elif coll > .66666666 and coll < .83333333:
        list_pre.update({'Collaborative Spirit': "Above Average"})
    else:
        list_pre.update({'Collaborative Spirit': "Outstanding"})

    if a > 0.0 and a < .16666:
        list_pre.update({'Accountability': "Not Assessed"})
    elif a > .16666 and a < .333333:
        list_pre.update({'Accountability': "Unacceptable"})
    elif a > .333333 and a < .50000:
        list_pre.update({'Accountability': "Margina"})
    elif a > .50000 and a < .66666666:
        list_pre.update({'Accountability': "Acceptable"})
    elif a > .66666666 and a < .83333333:
        list_pre.update({'Accountability': "Above Average"})
    else:
        list_pre.update({'Accountability': "Outstanding"})

    if d > 0.0 and d < .16666:
        list_pre.update({'Drive': "Not Assessed"})
    elif d > .16666 and d < .333333:
        list_pre.update({'Drive': "Unacceptable"})
    elif d > .333333 and d < .50000:
        list_pre.update({'Drive': "Margina"})
    elif d > .50000 and d < .66666666:
        list_pre.update({'Drive': "Acceptable"})
    elif d > .66666666 and d < .83333333:
        list_pre.update({'Drive': "Above Average"})
    else:
        list_pre.update({'Drive': "Outstanding"})        
        
    #print(list_pre)
    return list_pre


